import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    if (localStorage.getItem('listArray') == null) {
      this.state = {
        ip: '',
        list: [],
        info: []
      }
    } else {
      this.state = {
        ip: '',
        list: JSON.parse(localStorage.getItem('listArray')),
        info: JSON.parse(localStorage.getItem('ipData'))
      }
    }
  }
  handleSubmit(event) {
    event.preventDefault();
    // this.setState({ip: ''});

    let listArray = this.state.list;
    listArray.push(this.state.ip);

    localStorage.setItem('listArray', JSON.stringify(listArray));

    this.setState({
      list: JSON.parse(localStorage.getItem('listArray')),
    })

    let url = 'https://api.2ip.ua/geo.json?ip=';
    let ipData = this.state.info;
    fetch(url + this.state.ip) 
      .then(function(response) { 
      if (response.status !== 200) { 
      alert('Looks like there was a problem. Status Code: ' + 
      response.status); 
      return; 
      } 
      response.json().then(function(data) { 
        console.log(data); 
        ipData.push(data.city_rus);
        localStorage.setItem('ipData', JSON.stringify(ipData)); 

      }); 
      }) 
      .catch(function(err) { 
      console.log('Fetch Error :-S', err); 
      });

    this.setState({
      info: JSON.parse(localStorage.getItem('ipData')),
      ip: ''
      });
  }

  handleIpChange(event) {
    this.setState({ip: event.target.value});
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit.bind(this)}>
          <label>
            <p>Введите ip-адрес</p>
            <input 
            type="text"
            placeholder="Ip-адрес..."
            value={this.state.ip}
            onChange={this.handleIpChange.bind(this)} 
             />
          </label>
          <button>Submit</button>
        </form>
        <div>

          {this.state.info.map((info, index) => 
              <ul key={index}>
                <li key={index}>{info}</li>
              </ul>
            )}
        </div>
      </div>
      );
  }
}

export default App;
