import React, { Component } from 'react';

class CheckIp extends Component {
  constructor(props) {
    super(props);
    if (localStorage.getItem('listArray') == null) {
      this.state = {
        ip: '',
        list: [],
        info: []
      }
    } else if (localStorage.getItem('ipData') == null) {
        this.state = {
        ip: '',
        list: [],
        info: []
      }
    } else {
      this.state = {
        ip: '',
        list: JSON.parse(localStorage.getItem('listArray')),
        info: JSON.parse(localStorage.getItem('ipData'))
      }
    }
  }
  handleSubmit(event) {
    event.preventDefault();

    let listArray = this.state.list;
    let url = 'https://api.2ip.ua/geo.json?ip=';
    let ipData = this.state.info;
    console.log(this); 
    fetch(url + this.state.ip).then(function(response) {
        if (response.status !== 200) { 
        alert('Looks like there was a problem. Status Code: ' + 
        response.status); 
        return; 
        }

         response.json().then(function(data) {
          ipData.push(data.city_rus);
          console.log(data.ip); 
          localStorage.setItem('ipData', JSON.stringify(ipData)); 

          listArray.push(data.ip);

          localStorage.setItem('listArray', JSON.stringify(listArray));

            //     this.setState({
            //     list: JSON.parse(localStorage.getItem('listArray')),
            //     info: JSON.parse(localStorage.getItem('ipData')),
            //     ip: ''
            // });
        }); 
      }
      ) 
      // .catch(function(err) { 
      // console.log('Fetch Error :-S', err); 
      // });
      
        this.setState({
          list: JSON.parse(localStorage.getItem('listArray')),
          info: JSON.parse(localStorage.getItem('ipData')),
          ip: ''
      });

       
     // console.log(ipData);
  }

  handleIpChange(event) {
    this.setState({ip: event.target.value});
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit.bind(this)}>
          <label>
            <p>Введите ip-адрес</p>
            <input 
            type="text"
            placeholder="Ip-адрес..."
            value={this.state.ip}
            onChange={this.handleIpChange.bind(this)} 
             />
          </label>
          <button>Submit</button>
        </form>
        <div>
          {this.state.list.map((adress, index) => 
            this.state.info.map((city, index) => 
                  <ul key={index}>
                  <li key={index}>Ip-адресс: {adress} - Город: {city}</li>
                </ul>
              )
                
              )}
          
          
        </div>
      </div>
      );
  }
}

export default CheckIp;
